class App extends Component {

  state = {
    cases: []
  }

  componentDidMount() {
    axios.post('http://api.home:8080/casa/llegir')
      .then(res => {
        const cases = res.data;
        this.setState({ cases });
    })
  }

  render() {
    return (

        <div>
          <Header />
          <Cerca />
          <CarouselCards key="carouselCards" cases={this.state.cases}/>
          <Footer />                   
        </div>


    );
  }
}

export default App;
