import React, {Component} from 'react';
//import logo from './logo.svg';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.css';
import 'popper.js/dist/popper.js';
import CardCasa from './components/CardCasa';
import Header from './components/Header';


/*
{"id":2,"traduccioNom":"Son Manera","tradDescripcio":"A la preciosa finca de pedra natural Son Manera amb un disseny modern, garantiu que passareu les vacances perfectes. Experimenteu la Mallorca original des d\u2019una perspectiva completament nova a la finca amb molt bonica renovaci\u00f3 a prop de Montu\u00efri. Ja ens hem enamorat d\u2019aquesta casa al sol.\n","img_principal":"2_1.jpg"},{"id":3,"traduccioNom":"Son Binimelis","tradDescripcio":"A la preciosa finca de pedra natural Son Binimelis amb un disseny modern, garantiu que passareu les vacances perfectes. Experimenteu la Mallorca original des d\u2019una perspectiva completament nova a la finca amb molt bonica renovaci\u00f3 a prop de Manacor. Ja ens hem enamorat d\u2019aquesta casa al sol.\n","img_principal":"3_1.jpg"},{"id":4,"traduccioNom":"Can Joan","tradDescripcio":"A la preciosa finca de pedra natural Son Manera amb un disseny modern, garantiu que passareu les vacances perfectes. Experimenteu la Mallorca original des d\u2019una perspectiva completament nova a la finca amb molt bonica renovaci\u00f3 a prop de Montu\u00efri. Ja ens hem enamorat d\u2019aquesta casa al sol.\r\n","img_principal":"4_1.jpg"}
*/


class AppCerca extends Component {

  state = {
    cases: []
  }

  componentDidMount() {
    axios.post('http://api.home:8080/cases')
      .then(res => {
        const cases = res.data;
        this.setState({ cases });
    })
    console.log(this.state.cases);
  }

  render() {
    return (
      <div>
        <Header />
        {this.props.cases.map( function (casa, index){
            return (
                <div className={(index == 0 ? "carousel-item active" : " carousel-item")}>
                    <div className="col-md-4">
                        <CardCasa key={casa.id} img={casa.img_principal} nom={casa.traduccioNom} descripcio={casa.tradDescripcio} habitacions={casa.nHabitacions} banys={casa.nBanys}/>
                    </div>
                </div>
            );
        })}

      </div>
    );
  }
}

export default AppCerca;
